-- Creazione delle tabelle

CREATE TABLE tessera(
	codice int not null,
	totale_punti int not null,
	data_sottoscrizione date not null,
	ultimo_acquisto date not null,
	PRIMARY KEY (codice)
);

CREATE TABLE negozio(
	codice SERIAL PRIMARY KEY,
	indirizzo varchar(50) not null,
	provincia varchar(50) not null,
	comune varchar(50) not null
);

CREATE TABLE cliente(
	codice SERIAL,
	nome varchar(50) not null,
	cognome varchar(50) not null,
	indirizzo varchar(100),
	telefono int not null,
	tessera int not null,
	PRIMARY KEY (codice),
	FOREIGN KEY (tessera) REFERENCES tessera(codice)
);

CREATE TABLE prodotto(
	codice SERIAL,
    descrizione varchar(256) not null,
    categoria varchar(50) not null,
    prezzo int not null,
    id_negozio int,
		PRIMARY KEY (codice),
		FOREIGN KEY (id_negozio) REFERENCES negozio(codice)
);

CREATE TABLE scontrino(
		codice SERIAL,
		data_acquisto date,
		codice_negozio int not null,
    codice_prodotto int not null,
	    codice_cliente int not null,
		PRIMARY KEY (codice),
		FOREIGN KEY (codice_negozio) REFERENCES negozio(codice),
		FOREIGN KEY (codice_prodotto) REFERENCES prodotto(codice),
				FOREIGN KEY (codice_cliente) REFERENCES cliente(codice)
);

